mapper.update:
UPDATE BBS						
   SET GROUP_ORDER = GROUP_ORDER + 1
 WHERE GROUP_NO = #{groupNo}
   AND GROUP_ORDER > #{groupOrder}

UPDATE BBS
   SET STATE = 0
 WHERE BBS_NO = #{bbsNo}

UPDATE BBS
   SET BBS_TITLE = #{bbsTitle}, BBS_LAST_MODIFY_DATE = SYSDATE
 WHERE ID = #{id}
   AND BBS_NO = #{bbsNo}
