   SET SESSION_ID = #{sessionId}
     , SESSION_LIMIT_DATE = #{sessionLimitDate}
 WHERE ID = #{id}

UPDATE USERS
   SET PW = #{pw}
     , PW_MODIFY_DATE = SYSDATE


	 	USER_NO = #{userNo}


	 	AND ID = #{id}

mapper.select:
